import React from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './Login.module.css';

const Login = () => {
  const navigate = useNavigate();

  const handleLogin = (event) => {
    event.preventDefault();
    // Aqui você pode adicionar a lógica de autenticação
    // Se a autenticação for bem-sucedida, navegue para a página de menu
    navigate('/usuario/menu');
  };

  return (
    <div className={styles.container}>
      <div className={styles.box}>
        <h1 className={`${styles.title} mb-4 text-center`}>Portal do Funcionário</h1>
        <form onSubmit={handleLogin}>
          <div className="mb-3">
            <label htmlFor="cpf" className="form-label">CPF</label>
            <input type="text" className="form-control" id="cpf" placeholder="Digite seu CPF" />
          </div>
          <div className="mb-3">
            <label htmlFor="senha" className="form-label">Senha</label>
            <input type="password" className="form-control" id="senha" placeholder="Digite sua Senha" />
          </div>
          <button type="submit" className="btn btn-primary w-100">Entrar</button>
          <button type="button" className="btn btn-secondary w-100 mt-2" onClick={() => navigate('/')}>Voltar</button>

        </form>
      </div>
    </div>
  );
};

export default Login;
