import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './Login.module.css'; // Estilos adicionais personalizados
import 'bootstrap/dist/css/bootstrap.min.css'; // Importação do CSS do Bootstrap

const Login = () => {
  const navigate = useNavigate();
  const [cpf, setCpf] = useState('');

  // Função para formatar o CPF
  const formatCPF = (value) => {
    // Remove caracteres não numéricos
    const cleaned = ('' + value).replace(/\D/g, '');

    // Aplica a formatação de CPF (###.###.###-##)
    const formatted = cleaned.replace(/^(\d{3})(\d{3})(\d{3})(\d{2}).*/, '$1.$2.$3-$4');

    return formatted;
  };

  // Handler para alterações no campo CPF
  const handleCpfChange = (event) => {
    const formattedCpf = formatCPF(event.target.value);
    setCpf(formattedCpf);
  };

  return (
    <div className="container">
      <div className={`card ${styles.box}`}>
        <h1 className={`card-title ${styles.title}`}>Preencha os campos abaixo para acessar o Menu</h1>
        <form>
          <div className="form-group">
            <label className={`form-label ${styles.formLabel}`} htmlFor="cpf">CPF</label>
            <input
              type="text"
              className={`form-control ${styles.input}`}
              id="cpf"
              value={cpf}
              onChange={handleCpfChange}
              maxLength={14} // Limita o tamanho máximo do campo para a formatação
            />
          </div>
          <div className="form-group">
            <label className={`form-label ${styles.formLabel}`} htmlFor="senha">Senha</label>
            <input type="password" className={`form-control ${styles.input}`} id="senha" />
          </div>
          <button type="button" className={`btn btn-primary ${styles.btn}`} onClick={() => navigate('/Menu')}>
            Entrar
          </button>
          <button type="button" className={`btn btn-secondary ${styles.btn}`} onClick={() => navigate('/')}>
            Voltar
          </button>
        </form>
        <p className={`card-text ${styles.rodape}`}>Desenvolvido por Leonardo W O Dias</p>
      </div>
    </div>
  );
};

export default Login;
